package tssreward

import (
	"fmt"
	"github.com/mantlenetworkio/mantle/l2geth/common/hexutil"
	"log"
	"testing"
)

func TestPacketData(t *testing.T) {
	var data Data
	dataHex := ""
	decodedData, err := hexutil.Decode(dataHex)
	if err != nil {
		log.Fatal(err)
	}

	data.UnPackData(decodedData)
	fmt.Printf("%+v\n", data)

	dataHex2 := ""
	var data2 Data
	decodedData2, err := hexutil.Decode(dataHex2)
	if err != nil {
		log.Fatal(err)
	}
	data2.UnPackData(decodedData2)
	fmt.Printf("%+v", data2)

}
